class Solution {
public:
    struct Node {
        Node* links[26];
        int ans=1e9;
        bool containsKey(char ch) {
            return links[ch - 'a'] != NULL;
        }
        void put(char ch, Node* node) {
            links[ch - 'a'] = node;
        }
        Node* get(char ch) {
            return links[ch - 'a'];
        }
        void setEnd(int val) {
            ans=min(ans,val);
        }
        int isEnd() {
            return ans;
        }
     };

    class Trie {
    private:
        Node* root;

    public:
        Trie() {
            root = new Node();
        } 
        void insert(string &word,int val) {
            Node* node = root;
            for (int i = 0; i < word.length(); i++) {
                if (!node->containsKey(word[i])) {
                    node->put(word[i], new Node());
                }
                node = node->get(word[i]);
            }
            node->setEnd(val);
        }
        void search(string &word,int start,int sz,vector<int> &dp,int curr) {
            Node* node = root;
            for (int i = 0;i<word.size(); i++) {
                if(start+i>=sz) return ;
                if (!node->containsKey(word[start+i])) {
                    return ;
                }
                node = node->get(word[i+start]);
                int x=node->isEnd();
                dp[start+i]=min(dp[start+i],x+curr);
            }
        }
    };

    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
        int n=words.size(),sz=target.size();
        vector<int> dp(sz,1e9);
        Trie T;
        for(int i=0;i<n;i++){
            T.insert(words[i],costs[i]);
        }
        for(int i=0;i<sz;i++){
            int curr=0;
            if((i-1)>=0) curr=dp[i-1];
            T.search(target,i,sz,dp,curr);
        }
        if(dp[sz-1]==1e9) dp[sz-1]=-1;
        return dp[sz-1];
    }
};
