/**
 * @param {string} target
 * @param {string[]} words
 * @param {number[]} costs
 * @return {number}
 */
var minimumCost = function(target, words, costs) {
    const N = target.length
    const M = words.length
    const trie = createTrie()
    const dp = Array(N + 1).fill(Infinity)
    dp[0] = 0
    for (let i = 0; i < N; i++) {
        for (let j = i, node = trie; j >= 0 && node; j--) {
            const c = target[j]
            node = node[c]
            if (node?.leaf) {
                dp[i + 1] = Math.min(dp[i + 1], node.cost + dp[j])
            }
        }
    }
    return dp[N] === Infinity ? -1 : dp[N]

    function createTrie() {
        const root = {}
        for (let i = 0; i < M; i++) {
            const word = words[i]
            const cost = costs[i]
            for (let j = word.length - 1, node = root; j >= 0; j--) {
                const c = word[j]
                node = node[c] = node[c] || {cost: Infinity}
                if (j === 0) {
                    node.leaf = true
                    node.cost = Math.min(cost, node.cost)
                }
            }
        }
        return root
    }
};
