class Node{
    public:
        int i;
        int val;
        vector<Node*> children;
        Node():children(26, NULL){
            val = INT_MAX;
            i = -1;
        }

        void init(int i, int val){
            if(val < this->val){
                this->i = i;
                this->val = val;
            }
        } 
};

class Trie{
    public:
        Node* root;
        Trie(){
            root = new Node();
        }

        void add(Node* node, string& word, int i, int j, int c){
            if(j == word.length()){
                node->init(i, c);
                return;
            }

            if(node->children[word[j] - 'a'] == NULL){
                node->children[word[j] - 'a'] = new Node();
            }

            add(node->children[word[j] - 'a'], word, i, j+1, c);
        }

        void addWord(string& word, int i, int c){
            add(root, word, i, 0, c);
        }
};

class Solution {
public:

    int rec(string& target, int i, vector<int>& dp, Trie* t){
        if(i >= target.length()){
            return 0;
        }

        if(dp[i] != -1){
            return dp[i];
        }

        Node* node = t->root;
        int j = i;
        int ans = INT_MAX;
        while(j <= target.length() && node != NULL){
            if(node->i != -1){

                int k =  rec(target, j, dp, t);
                if(k != INT_MAX){
                    ans = min(ans, k + node->val);
                }
            }
            if(j == target.length()){
                break;
            }
            node = node->children[target[j] - 'a'];
            j++;
        }

        dp[i] = ans;
        return ans;
    }

    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
        Trie* t = new Trie();
        for(int i = 0; i < words.size(); i++){
            t->addWord(words[i], i, costs[i]);
        }
        vector<int> dp(target.length(), -1);
        int ans = rec(target, 0, dp, t);
        return ans == INT_MAX ? -1 : ans;
        // return 0;
    }
};
