class Solution {
public:
    struct TrieNode {
    unordered_map<char, TrieNode*> children;
    vector<int> wordIndices;
};

class Trie {
public:
    TrieNode* root;
    Trie() {
        root = new TrieNode();
    }
    void insert(string word, int index) {
        TrieNode* node = root;
        for (char c : word) {
            if (!node->children.count(c)) {
                node->children[c] = new TrieNode();
            }
            node = node->children[c];
        }
        node->wordIndices.push_back(index);
    }
};
    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
        int n = target.length();
        Trie trie;

        // Insert words into the Trie
        for (int i = 0; i < words.size(); ++i) {
            trie.insert(words[i], i);
        }

        vector<int> dp(n + 1, INT_MAX);
        dp[0] = 0;

        // DP with Trie search
        for (int i = 0; i < n; ++i) {
            if (dp[i] == INT_MAX) continue;
            TrieNode* node = trie.root;
            for (int j = i; j < n; ++j) {
                if (!node->children.count(target[j])) break;
                node = node->children[target[j]];
                for (int wordIndex : node->wordIndices) {
                    int wordLen = words[wordIndex].length();
                    int cost = costs[wordIndex];
                    dp[j + 1] = min(dp[j + 1], dp[i] + cost);
                }
            }
        }

        return dp[n] == INT_MAX ? -1 : dp[n];
    }
};
