import java.util.Arrays;
import java.util.HashMap;
import java.util.Map;

class Solution {
    class TrieNode {
        Map<Character, TrieNode> children = new HashMap<>();
        int cost = Integer.MAX_VALUE;
    }

    private TrieNode root;

    private void insert(String word, int cost) {
        TrieNode node = root;
        for (char c : word.toCharArray()) {
            node = node.children.computeIfAbsent(c, k -> new TrieNode());
        }
        node.cost = Math.min(node.cost, cost);
    }

    public int minimumCost(String target, String[] words, int[] costs) {
        int n = target.length();
        root = new TrieNode();

        for (int i = 0; i < words.length; i++) {
            insert(words[i], costs[i]);
        }

        int[] dp = new int[n + 1];
        Arrays.fill(dp, Integer.MAX_VALUE);
        dp[0] = 0;

        for (int i = 0; i < n; i++) {
            if (dp[i] == Integer.MAX_VALUE) continue;
            TrieNode node = root;
            for (int j = i; j < n; j++) {
                node = node.children.get(target.charAt(j));
                if (node == null) break;
                if (node.cost != Integer.MAX_VALUE) {
                    dp[j + 1] = Math.min(dp[j + 1], dp[i] + node.cost);
                }
            }
        }

        return dp[n] == Integer.MAX_VALUE ? -1 : dp[n];
    }
}
