import java.util.*;

class Solution {
    private static class TrieNode {
        TrieNode[] children;
        int cost;

        TrieNode() {
            this.children = new TrieNode[26];
            this.cost = Integer.MAX_VALUE;
        }
    }

    private TrieNode buildTrie(String[] words, int[] costs) {
        TrieNode root = new TrieNode();
        for (int i = 0; i < words.length; i++) {
            TrieNode node = root;
            for (char c : words[i].toCharArray()) {
                if (node.children[c - 'a'] == null) {
                    node.children[c - 'a'] = new TrieNode();
                }
                node = node.children[c - 'a'];
            }
            node.cost = Math.min(node.cost, costs[i]);
        }
        return root;
    }

    public int minimumCost(String target, String[] words, int[] costs) {
        TrieNode root = buildTrie(words, costs);
        Integer[] memo = new Integer[target.length() + 1];
        int result = dfs(target, 0, root, memo);
        return result == Integer.MAX_VALUE ? -1 : result;
    }

    private int dfs(String target, int start, TrieNode root, Integer[] memo) {
        if (start == target.length()) {
            return 0;
        }
        if (memo[start] != null) {
            return memo[start];
        }

        TrieNode node = root;
        int minCost = Integer.MAX_VALUE;
        for (int i = start; i < target.length(); i++) {
            char c = target.charAt(i);
            if (node.children[c - 'a'] == null) {
                break;
            }
            node = node.children[c - 'a'];
            if (node.cost != Integer.MAX_VALUE) {
                int cost = dfs(target, i + 1, root, memo);
                if (cost != Integer.MAX_VALUE) {
                    minCost = Math.min(minCost, cost + node.cost);
                }
            }
        }
        memo[start] = minCost;
        return minCost;
    }
}
