from typing import List

class Solution:
    def minimumCost(self, target: str, words: List[str], costs: List[int]) -> int:
        n = len(target)
        m = len(words)
        count = {}
        for i, w in enumerate(words):
            count[w] = min(count.get(w, float('inf')), costs[i])

        array = [float('inf')] * (n + 1)
        array[0] = 0
        for i in range(1, n + 1):
            for w in count:
                if i >= len(w) and target[i - len(w):i] == w:
                    array[i] = min(array[i], array[i - len(w)] + count[w])

        return array[n] if array[n] != float('inf') else -1
