struct TrieNode {
    unordered_map<char, TrieNode*> children;
    vector<pair<int, int>> wordIndicesAndCosts;
};

class Trie {
public:
    Trie() : root(new TrieNode()) {}

    void insert(const string& word, int index, int cost) {
        TrieNode* node = root;
        for (char c : word) {
            if (!node->children.count(c)) {
                node->children[c] = new TrieNode();
            }
            node = node->children[c];
        }
        node->wordIndicesAndCosts.push_back({index, cost});
    }

    TrieNode* getRoot() {
        return root;
    }

private:
    TrieNode* root;
};

class Solution {
public:
    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
        int n = target.size();
        vector<int> dp(n + 1, INT_MAX);
        dp[0] = 0;

        Trie trie;
        for (int i = 0; i < words.size(); ++i) {
            trie.insert(words[i], i, costs[i]);
        }

        for (int i = 0; i < n; ++i) {
            if (dp[i] == INT_MAX) continue;
            TrieNode* node = trie.getRoot();
            for (int j = i; j < n; ++j) {
                if (!node->children.count(target[j])) break;
                node = node->children[target[j]];
                for (const auto& [index, cost] : node->wordIndicesAndCosts) {
                    if (j + 1 <= n) {
                        dp[j + 1] = min(dp[j + 1], dp[i] + cost);
                    }
                }
            }
        }

        return dp[n] == INT_MAX ? -1 : dp[n];
    }
};
