class TrieNode {
public:
    unordered_map<char, TrieNode*> children;
    int minCost;

    TrieNode() : minCost(INT_MAX) {}
};

class Trie {
public:
    TrieNode* root;

    Trie() {
        root = new TrieNode();
    }

    void add(const string& word, int cost) {
        TrieNode* node = root;
        for (char c : word) {
            if (!node->children.count(c)) {
                node->children[c] = new TrieNode();
            }
            node = node->children[c];
        }
        node->minCost = min(node->minCost, cost);
    }
};

class Solution {
public:
    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
        int n = target.size();
        Trie trie;

        for (int i = 0; i < words.size(); ++i) {
            trie.add(words[i], costs[i]);
        }

        vector<int> dp(n + 1, INT_MAX);
        dp[0] = 0;

        for (int i = 0; i < n; ++i) {
            if (dp[i] == INT_MAX) continue;
            TrieNode* node = trie.root;
            for (int j = i; j < n; ++j) {
                char c = target[j];
                if (!node->children.count(c)) break;
                node = node->children[c];
                if (node->minCost != INT_MAX) {
                    dp[j + 1] = min(dp[j + 1], dp[i] + node->minCost);
                }
            }
        }

        return dp[n] == INT_MAX ? -1 : dp[n];
    }
};
