class Solution {
public:



using ll = long long;
unordered_map<string,ll>mp;


    struct TrieNode{
        char val;
        ll cnt;
        ll endsWith;

        ll cst;

        TrieNode* child[26];
    };

    TrieNode* root;

    TrieNode* getNode(char c)
    {
        TrieNode* tt = new TrieNode;
        tt->val = c;
        tt->cnt = 0;
        tt->endsWith = 0;
        tt->cst = 1e9;

        for(ll i=0;i<26;i++) tt->child[i]=NULL;

        return tt;
    }




    void insert(string word) {
        TrieNode* tt = root;

        for(auto c:word)
        {
            if(tt->child[c-'a']==NULL)
            {
                tt->child[c-'a'] = getNode(c);

            }

            tt->child[c-'a']->cnt++;
            tt = tt->child[c-'a'];
        }

        tt->endsWith = 1;
        tt->cst = min(tt->cst, mp[word]);


    }

    bool search(string word) {
        TrieNode* tt = root;

        for(auto c:word)
        {
            if(tt->child[c-'a']==NULL) return 0;
            tt = tt->child[c-'a'];
        }

        return tt->endsWith>0;
    }

    bool startsWith(string prefix) {
        TrieNode* tt = root;

        for(auto c:prefix)
        {
            if(tt->child[c-'a']==NULL) return 0;
            tt = tt->child[c-'a'];
        }

        return tt->cnt>0;
    }

    ll n;


    vector<ll>dp;


    int minimumCost(string tt, vector<string>& arr, vector<int>& cc) {
        mp.clear();
        n = tt.length();
        dp.clear();dp.resize(n+1,1e9);
        dp[0]=0;

       root = getNode('/');

        for(int i=0;i<arr.size();i++)
        {
            string val = arr[i];

            if(!mp.count(val))
            mp[val]=cc[i];
            else
            {
                mp[val]=min(mp[val],1ll*cc[i]);
            }

        }

        for(auto &s:arr) insert(s);

        // cout<<search("def")<<endl;

        for(int i=0;i<n;i++)
        {

            TrieNode* rr = root;

            for(int j=i;j<n;j++)
            {
                if(rr->child[tt[j]-'a']==NULL) break;
                rr = rr->child[tt[j]-'a'];


                if(rr->cst!=1e9)
                {
                    dp[j+1]=min(dp[j+1],dp[i]+rr->cst);
                }
            }

        }

        for(int i=0;i<n;i++) cout<<dp[i]<<" ";cout<<endl;
        ll ans = dp[n];

        if(ans>=1e9) return -1;

        return ans;


    }
};
