class Trie {
public:
    vector<Trie*> children;
    int cost;
    Trie() : children(26, nullptr), cost(INT_MAX) {}
};

class Solution {
public:
    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
        Trie* root = new Trie();
        for (int i = 0; i < words.size(); ++i) {
            Trie* node = root;
            for (char c : words[i]) {
                if (!node->children[c - 'a']) node->children[c - 'a'] = new Trie();
                node = node->children[c - 'a'];
            }
            node->cost = min(node->cost, costs[i]);
        }

        vector<int> dp(target.size() + 1, INT_MAX);
        dp[0] = 0;
        for (int i = 0; i < target.size(); ++i) {
            if (dp[i] == INT_MAX) continue;
            Trie* node = root;
            for (int j = i; j < target.size(); ++j) {
                if (!node->children[target[j] - 'a']) break;
                node = node->children[target[j] - 'a'];
                if (node->cost != INT_MAX) dp[j + 1] = min(dp[j + 1], dp[i] + node->cost);
            }
        }

        return dp[target.size()] == INT_MAX ? -1 : dp[target.size()];
    }
};
