class TrieNode:
    def __init__(self):
        self.children = {}
        self.cost = None

class Trie:
    def __init__(self):
        self.root = TrieNode()

    def insert(self, word, cost):
        node = self.root
        for char in word:
            if char not in node.children:
                node.children[char] = TrieNode()
            node = node.children[char]
        if (node.cost != None):
            node.cost = min(node.cost, cost)
        else:
            node.cost = cost

    def search(self, target, start):
        node = self.root
        results = []
        for i in range(start, len(target)):
            if target[i] not in node.children:
                break
            node = node.children[target[i]]
            if node.cost is not None:
                results.append((i + 1, node.cost))
        return results

class Solution:
    def minimumCost(self, target: str, words: List[str], costs: List[int]) -> int:
        n = len(target)
        dp = [float('inf')] * (n + 1)
        dp[0] = 0


        trie = Trie()
        for word, cost in zip(words, costs):
            trie.insert(word, cost)


        for i in range(n):
            if dp[i] == float('inf'):
                continue

            match_words = trie.search(target, i)
            for (end, cost) in match_words:
                dp[end] = min(dp[end], dp[i] + cost)


        return dp[-1] if dp[-1] != float('inf') else -1
