#include<bits/stdc++.h>
using namespace std;
class Solution {
public:
    class TN {
public:
    unordered_map<char, TN*> c;
    int cost;

    TN() {
        cost = -1;
    }
};

class T {
public:
    TN* r;

    T() {
        r = new TN();
    }
void myi(string w,int cost){
     TN* c = r;
        for (char ch : w) {
            if (c->c.find(ch) == c->c.end()) {
                c->c[ch] = new TN();
            }
            c = c->c[ch];
        }
        if (c->cost == -1 || cost < c->cost) {
            c->cost = cost;
        }
}
    void i(string w, int cost) {
       myi(w,cost);
    }

    int g(string p) {
        TN* c = r;
        for (char ch : p) {
            if (c->c.find(ch) == c->c.end()) {
                return -1;
            }
            c = c->c[ch];
        }
        return c->cost;
    }
};
    int mystkfun(string t, vector<string>& w, vector<int>& costs) {
    T trie;
    int n = w.size();

    for (int i = 0; i < n; ++i) {
        trie.i(w[i], costs[i]);
    }

    int m = t.length();
    vector<int> dp(m + 1, -1);

    dp[0] = 0;

    for (int i = 0; i < m; ++i) {
        if (dp[i] == -1) continue;
        TN* c = trie.r;
        for (int j = i; j < m; ++j) {
            char ch = t[j];
            if (c->c.find(ch) == c->c.end()) break;
            c = c->c[ch];
            if (c->cost != -1) {
                if (dp[j + 1] == -1 || dp[i] + c->cost < dp[j + 1]) {
                    dp[j + 1] = dp[i] + c->cost;
                }
            }
        }
    }

    return dp[m];
}

    int minimumCost(string tar, vector<string>& wors, vector<int>& coss) {
       return  mystkfun(tar,wors,coss);
    }
};
