class N {
public:
    N* c[26] = {};
    int w = -1;
};

class Solution {
public:
    int minimumCost(string t, vector<string>& w, vector<int>& o) {
        int n = t.size();
        N* r = new N();

        // Build trie
        for (int i = 0; i < w.size(); ++i) {
            N* node = r;
            for (char ch : w[i]) {
                if (!node->c[ch - 'a']) node->c[ch - 'a'] = new N();
                node = node->c[ch - 'a'];
            }
            if (node->w == -1 || o[i] < o[node->w]) node->w = i;
        }

        vector<int> dp(n + 1, INT_MAX);
        dp[0] = 0;

        for (int i = 0; i < n; ++i) {
            if (dp[i] == INT_MAX) continue;
            N* node = r;
            for (int j = i; j < n; ++j) {
                if (!node->c[t[j] - 'a']) break;
                node = node->c[t[j] - 'a'];
                if (node->w != -1) {
                    dp[j + 1] = min(dp[j + 1], dp[i] + o[node->w]);
                }
            }
        }

        return dp[n] == INT_MAX ? -1 : dp[n];
    }
};
