class Solution {
    class TrieNode {
        TrieNode[] children = new TrieNode[26];  
        int cost=Integer.MAX_VALUE;      
    }

    TrieNode root = new TrieNode();

    void insert(String str, int cost) {
        var node = root;
        for(char c: str.toCharArray()) {
            if(node.children[c-'a']==null) { node.children[c-'a']=new TrieNode(); }
            node = node.children[c-'a'];
        }
        node.cost = Math.min(node.cost, cost);
    }

    public int minimumCost(String target, String[] words, int[] costs) {
        for(int i=0; i<words.length; i++) {
            insert(words[i], costs[i]);
        }
        Integer[] dp = new Integer[target.length()+1];
        var output = getMinCost(target, 0, dp);
        return output == Integer.MAX_VALUE ? -1 : output;
    }

    int getMinCost(String target, int idx, Integer[] dp) {
        int minCost=Integer.MAX_VALUE;

        if(dp[idx]!=null) return dp[idx];

        var node = root;
        for(int i=idx; i<target.length(); i++) {
            char ch = target.charAt(i);
            if(node.children[ch-'a']==null) break;

            node = node.children[ch-'a'];
            if(node.cost!=Integer.MAX_VALUE) {
                if(i==target.length()-1) { minCost = Math.min(minCost, node.cost); }
                else {
                    var childMinCost =  getMinCost(target, i+1, dp);
                    if(childMinCost != Integer.MAX_VALUE) { minCost = Math.min(minCost, node.cost + childMinCost); }
                }              
            }
        }

        return dp[idx]=minCost;
    }
}
