import java.util.*;

class Solution {
    public int minimumCost(String target, String[] words, int[] costs) {
        int n = target.length();
        int[] dp = new int[n + 1];
        Arrays.fill(dp, Integer.MAX_VALUE);
        dp[0] = 0;

        Map<String, Integer> wordCost = new HashMap<>();
        for (int i = 0; i < words.length; i++) {
            wordCost.put(words[i], Math.min(wordCost.getOrDefault(words[i], Integer.MAX_VALUE), costs[i]));
        }

        for (int i = 0; i < n; i++) {
            if (dp[i] == Integer.MAX_VALUE) continue;
            for (String word : wordCost.keySet()) {
                if (target.startsWith(word, i)) {
                    int end = i + word.length();
                    dp[end] = Math.min(dp[end], dp[i] + wordCost.get(word));
                }
            }
        }

        return dp[n] == Integer.MAX_VALUE ? -1 : dp[n];
    }
}
