class TrieNode {
public:
    unordered_map<char, TrieNode*> children;
    vector<pair<int, int>> wordEnds;
};

class Trie {
public:
    TrieNode* root;

    Trie() {
        root = new TrieNode();
    }

    void insertWord(string word, int index, int cost) {
        TrieNode* node = root;
        for (char c : word) {
            if (!node->children.count(c)) {
                node->children[c] = new TrieNode();
            }
            node = node->children[c];
        }
        node->wordEnds.push_back({index, cost});
    }
};

class Solution {
public:
    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
        int targetLength = target.size();
        vector<int> dp(targetLength + 1, INT_MAX);
        dp[0] = 0;

        Trie trie;
        for (int i = 0; i < words.size(); ++i) {
            trie.insertWord(words[i], i, costs[i]);
        }

        for (int i = 0; i < targetLength; ++i) {
            if (dp[i] == INT_MAX) {
                continue;
            }

            TrieNode* node = trie.root;
            for (int j = i; j < targetLength; ++j) {
                char currentChar = target[j];
                if (!node->children.count(currentChar)) {
                    break;
                }
                node = node->children[currentChar];
                for (auto& pair : node->wordEnds) {
                    int wordLength = words[pair.first].size();
                    if (i + wordLength <= targetLength) {
                        dp[i + wordLength] = min(dp[i + wordLength], dp[i] + pair.second);
                    }
                }
            }
        }

        return dp[targetLength] == INT_MAX ? -1 : dp[targetLength];
    }
};
