#include <vector>
#include <string>
#include <climits>
#include <unordered_map>

using namespace std;

class TrieNode {
public:
    unordered_map<char, TrieNode*> children;
    int cost = -1;
};

class Solution {
private:
    TrieNode* buildTrie(const vector<string>& words, const vector<int>& costs) {
        TrieNode* root = new TrieNode();
        for (int i = 0; i < words.size(); ++i) {
            TrieNode* node = root;
            for (char c : words[i]) {
                if (!node->children.count(c)) {
                    node->children[c] = new TrieNode();
                }
                node = node->children[c];
            }
            if (node->cost == -1) {
                node->cost = costs[i];
            } else {
                node->cost = min(node->cost, costs[i]);
            }
        }
        return root;
    }

public:
    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
        int n = target.size();
        vector<int> dp(n + 1, INT_MAX);
        dp[0] = 0;

        TrieNode* root = buildTrie(words, costs);

        for (int i = 0; i < n; ++i) {
            if (dp[i] == INT_MAX) continue;
            TrieNode* node = root;
            for (int j = i; j < n; ++j) {
                if (!node->children.count(target[j])) break;
                node = node->children[target[j]];
                if (node->cost != -1) {
                    dp[j + 1] = min(dp[j + 1], dp[i] + node->cost);
                }
            }
        }

        return dp[n] == INT_MAX ? -1 : dp[n];
    }
};
