class TrieNode {
public:
    unordered_map<char, TrieNode*> children;
    int cost;
    TrieNode() : cost(-1) {}
};

class Solution {
public:
    void insert(TrieNode* root, const string& word, int cost) {
        TrieNode* node = root;
        for (char c : word) {
            if (!node->children.count(c)) {
                node->children[c] = new TrieNode();
            }
            node = node->children[c];
        }
        if (node->cost == -1 || node->cost > cost) {
            node->cost = cost;
        }
    }

    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
        TrieNode* root = new TrieNode();
        for (int i = 0; i < words.size(); ++i) {
            insert(root, words[i], costs[i]);
        }

        int n = target.size();
        vector<long long> dp(n + 1, LLONG_MAX);
        dp[0] = 0;

        for (int i = 0; i < n; ++i) {
            if (dp[i] == LLONG_MAX) continue;
            TrieNode* node = root;
            for (int j = i; j < n; ++j) {
                if (!node->children.count(target[j])) break;
                node = node->children[target[j]];
                if (node->cost != -1) {
                    dp[j + 1] = min(dp[j + 1], dp[i] + node->cost);
                }
            }
        }

        return dp[n] == LLONG_MAX ? -1 : dp[n];
    }
};
