class TrieNode_Goku {
public:
    unordered_map<char, TrieNode_Goku*> bachche_Goku;
    vector<pair<int, int>> shabdKhatam_Goku;
};

class Trie_Goku {
public:
    TrieNode_Goku* jad_Goku;

    Trie_Goku() {
        jad_Goku = new TrieNode_Goku();
    }

    void jodo_Goku(string shabd_Goku, int suuchk_Goku, int lagat_Goku) {
        TrieNode_Goku* node_Goku = jad_Goku;
        for (char akshar_Goku : shabd_Goku) {
            if (!node_Goku->bachche_Goku.count(akshar_Goku)) {
                node_Goku->bachche_Goku[akshar_Goku] = new TrieNode_Goku();
            }
            node_Goku = node_Goku->bachche_Goku[akshar_Goku];
        }
        node_Goku->shabdKhatam_Goku.push_back({suuchk_Goku, lagat_Goku});
    }
};


class Solution {
public:
    int minimumCost(string lakshya_Goku, vector<string>& shabdon_Goku, vector<int>& lagat_Goku) {
           int lambai_Goku = lakshya_Goku.size();
        vector<int> dp_Goku(lambai_Goku + 1, INT_MAX);
        dp_Goku[0] = 0;

        Trie_Goku trie_Goku;
        for (int i_Goku = 0; i_Goku < shabdon_Goku.size(); ++i_Goku) {
            trie_Goku.jodo_Goku(shabdon_Goku[i_Goku], i_Goku, lagat_Goku[i_Goku]);
        }

        for (int i_Goku = 0; i_Goku < lambai_Goku; ++i_Goku) {
            if (dp_Goku[i_Goku] == INT_MAX) {
                continue;
            }

            TrieNode_Goku* node_Goku = trie_Goku.jad_Goku;
            for (int j_Goku = i_Goku; j_Goku < lambai_Goku; ++j_Goku) {
                if (!node_Goku->bachche_Goku.count(lakshya_Goku[j_Goku])) {
                    break;
                }
                node_Goku = node_Goku->bachche_Goku[lakshya_Goku[j_Goku]];
                for (auto& p_Goku : node_Goku->shabdKhatam_Goku) {
                    int shabdLambai_Goku = shabdon_Goku[p_Goku.first].size();
                    if (i_Goku + shabdLambai_Goku <= lambai_Goku) {
                        dp_Goku[i_Goku + shabdLambai_Goku] = min(dp_Goku[i_Goku + shabdLambai_Goku], dp_Goku[i_Goku] + p_Goku.second);
                    }
                }
            }
        }

        return dp_Goku[lambai_Goku] == INT_MAX ? -1 : dp_Goku[lambai_Goku];
    }
};
