class Solution {
public:
    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
        vector<pair<int, vector<int>>> trieNodes;
        trieNodes.push_back({-1, vector<int>(26,-1)});
        for(int i = 0; i < words.size(); i++) {
            int node = 0;
            //cout << words[i] << endl;
            for(auto c: words[i]) {
                if(trieNodes[node].second[c-'a'] < 0) {
                    trieNodes.push_back({-1, vector<int>(26,-1)});
                    trieNodes[node].second[c-'a'] = trieNodes.size()-1;
                }
                node = trieNodes[node].second[c-'a'];
                //cout << c << " " << node << endl;
            }
            if(trieNodes[node].first<0)trieNodes[node].first=costs[i];
            else trieNodes[node].first=min(trieNodes[node].first,costs[i]);
        }

        vector<int> dp(target.size()+1, -1);
        dp[0]=0;
        for(int i = 0; i < target.size(); i++) {
            if(dp[i]<0) continue;
            int node = 0;
            for(int j = 0; i + j < target.size(); j++) {
                //cout << i << " " << j << " " << node << endl;
                if(trieNodes[node].second[target[i+j]-'a'] < 0) break;

                node = trieNodes[node].second[target[i+j]-'a']; 
                if(trieNodes[node].first >= 0) {
                    if(dp[i+j+1] < 0) dp[i+j+1] = trieNodes[node].first + dp[i];
                    else dp[i+j+1] = min(dp[i+j+1], trieNodes[node].first + dp[i]);
                }
            }
        }

        return dp[target.size()];
    }
};
