class Solution {
    class TrieNode {
        TrieNode[] children = new TrieNode[26];
        int minCost = Integer.MAX_VALUE;
    }

    public int minimumCost(String target, String[] words, int[] costs) {
        TrieNode root = buildTrie(words, costs);
        int n = target.length();
        int[] dp = new int[n + 1];
        Arrays.fill(dp, Integer.MAX_VALUE);
        dp[0] = 0;

        for (int i = 0; i < n; i++) {
            if (dp[i] == Integer.MAX_VALUE) continue;
            TrieNode node = root;
            for (int j = i; j < n; j++) {
                char c = target.charAt(j);
                if (node.children[c - 'a'] == null) break;
                node = node.children[c - 'a'];
                if (node.minCost < Integer.MAX_VALUE) {
                    long newCost = (long) dp[i] + node.minCost;
                    if (newCost < Integer.MAX_VALUE) {
                        dp[j + 1] = Math.min(dp[j + 1], (int) newCost);
                    }
                }
            }
        }

        return dp[n] == Integer.MAX_VALUE ? -1 : dp[n];
    }

    private TrieNode buildTrie(String[] words, int[] costs) {
        TrieNode root = new TrieNode();
        for (int i = 0; i < words.length; i++) {
            TrieNode node = root;
            for (char c : words[i].toCharArray()) {
                int index = c - 'a';
                if (node.children[index] == null) {
                    node.children[index] = new TrieNode();
                }
                node = node.children[index];
            }
            node.minCost = Math.min(node.minCost, costs[i]);
        }
        return root;
    }
}
