#include <iostream>
#include <vector>
#include <unordered_map>
#include <utility>
#include <climits>

using namespace std;

//why same solution in java is giving TLE ???

struct TrieNode {
    TrieNode* children[26];
    vector<pair<int, int>> last;

    TrieNode() {
        for (int i = 0; i < 26; ++i) {
            children[i] = nullptr;
        }
    }
};

class Trie {
public:
    TrieNode* root;

    Trie() {
        root = new TrieNode();
    }

    void insert(string word, int index, int cost) {
        TrieNode* node = root;
        for (char c : word) {
            int idx = c - 'a';
            if (node->children[idx] == nullptr) {
                node->children[idx] = new TrieNode();
            }
            node = node->children[idx];
        }
        node->last.push_back({index, cost});
    }
};

class Solution {
public:
    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
        int n = target.size();
        vector<int> dp(n + 1, (int)(1e9));
        dp[0] = 0;

        Trie trie;
        for (int i = 0; i < words.size(); ++i) {
            trie.insert(words[i], i, costs[i]);
        }

        for (int i = 0; i < n; ++i) {
            if (dp[i] == (int)(1e9)) {
                continue;
            }

            TrieNode* node = trie.root;
            for (int j = i; j < n; ++j) {
                char c = target[j];
                int idx = c - 'a';
                if (node->children[idx] == nullptr) {
                    break;
                }
                node = node->children[idx];
                for (auto& p : node->last) {
                    int temp_length = words[p.first].size();
                    if (i + temp_length <= n) {
                        dp[i + temp_length] = min(dp[i + temp_length], dp[i] + p.second);
                    }
                }
            }
        }

        if(dp[n] == (int)(1e9)) return -1;
        return dp[n];
    }
};
