bool match(string &s,int i,vector<string>& words,int idx,long long hash,vector<long long > &pre)
{

    int n=s.length();
    int m=words[idx].length();
    int r=i+m-1;
    long long  val=pre[r];
    if(i-1>=0)
        val-=pre[i-1];
    if(hash!=val)
        return false;
    if(i+m-1>=n)
        return false;
    return true;
    for(int l=0;l<m;l++)
    {
        if(s[i+l]!=words[idx][l])
            return false;
    }
    return true;
}

long long  fun(string &s)
{
    int n=s.length();
    long long ans=0;
    for(auto i:s)
    {
        ans+=1ll*(i-'a')*(pow(i-'a',4));
    }
    return ans;
}
class Solution {
public:
    int minimumCost(string target, vector<string>& words, vector<int>& costs) {

        map<char,vector<int>> mep;
        int n=costs.size();
         vector<long long > hash;
        for(int i=0;i<n;i++)
        {
            mep[words[i][0]].push_back(i);
            hash.push_back(fun(words[i]));
        }


        int m=target.size();

        vector<long long > pre(m,0);
        long long  curr=0;
        for(int i=0;i<m;i++)
        {
            curr+=(target[i]-'a')*1ll*(pow(target[i]-'a',4));
            pre[i]=curr;
        }
        vector<int> dp(m+1,INT_MAX);
        dp[m]=0;
        for(int i=m-1;i>=0;i--)
        {
            if(mep[target[i]].size()==0)
                continue;
            for(auto idx:mep[target[i]])
            {
                if(i+words[idx].size()<=m&&dp[i+words[idx].size()]!=INT_MAX&&match(target,i,words,idx,hash[idx],pre))
                {
                    dp[i]=min(dp[i],dp[i+words[idx].size()]+costs[idx]);
                }
            }
        }


        if(dp[0]==INT_MAX)
            return -1;
        return dp[0];

    }
};
