class Solution:
    def minimumCost(self, target: str, words: List[str], costs: List[int]) -> int:

        costMap = {}
        for w, c in zip(words,costs):
            if w not in costMap:
                costMap[w] = c
            costMap[w] = min(c,costMap[w])

        dp = [float('inf') for ch in target]
        dp.append(0)

        for index in range(len(target)):
            for word in costMap:
                if word == target[index - len(word) + 1:index + 1]:
                    dp[index] = min(dp[index], dp[index - len(word)] + costMap[word])
        print(dp)
        return dp[-2] if dp[-2] != float('inf') else -1
