class TrieNode:
    def __init__(self):
        self.children = {}
        self.isWord = False
        self.word = ""
        self.cost = float('inf')

class Trie:
    def __init__(self):
        self.root = TrieNode()

    def insert(self, word, cost):
        node = self.root
        for c in word:
            if c not in node.children:
                newNode = TrieNode()
                node.children[c] = newNode
            node = node.children[c]
        node.isWord = True
        node.word = word
        node.cost = min(node.cost, cost)

class Solution:
    def minimumCost(self, target: str, words: List[str], costs: List[int]) -> int:
        trie = Trie()
        for w, c in zip(words, costs):
            trie.insert(w, c)
        n = len(target)
        dp = [float('inf')] * (n + 1)
        dp[0] = 0

        for i in range(n):
            if dp[i] == float('inf'):
                continue
            node = trie.root
            for j in range(i, n):
                if target[j] not in node.children:
                    break
                node = node.children[target[j]]
                if node.isWord:
                    dp[j + 1] = min(dp[j + 1], dp[i] + node.cost)

        return dp[n] if dp[n] != float('inf') else -1
