class Solution:
    def minimumCost(self, target: str, words: List[str], costs: List[int]) -> int:
        wordtocost = {}
        for word, cost in zip(words, costs):
            if word not in wordtocost or cost < wordtocost[word]:
                wordtocost[word] = cost

        n = len(target)
        dp = [float('inf')] * (n + 1)
        dp[0] = 0

        for i in range(1, n + 1):
            for word, cost in wordtocost.items():
                if target[i-len(word):i] == word:
                    dp[i] = min(dp[i], dp[i-len(word)] + cost)

        return dp[n] if dp[n] < float('inf') else -1
