class TrieNode {
public:
    unordered_map<char, TrieNode*> children;
    vector<pair<int, int>> wordEnds;
};

class Trie {
public:
    TrieNode* root;

    Trie() {
        root = new TrieNode();
    }

    void insertWord(string word, int idx, int cost) {
        TrieNode* node = root;
        for (char c : word) {
            if (!node->children.count(c)) {
                node->children[c] = new TrieNode();
            }
            node = node->children[c];
        }
        node->wordEnds.push_back({idx, cost});
    }
};



class Solution {
public:
    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
          int n = target.size();
        vector<int> dp(n + 1, INT_MAX);
        dp[0] = 0;

        Trie trie;
        for (int i = 0; i < words.size(); ++i) {
            trie.insertWord(words[i], i, costs[i]);
        }

        for (int i = 0; i < n; ++i) {
            if (dp[i] == INT_MAX) {
                continue;
            }

            TrieNode* node = trie.root;
            for (int j = i; j < n; ++j) {
                if (!node->children.count(target[j])) {
                    break;
                }
                node = node->children[target[j]];
                for (auto& p : node->wordEnds) {
                    int wordLen = words[p.first].size();
                    if (i + wordLen <= n) {
                        dp[i + wordLen] = min(dp[i + wordLen], dp[i] + p.second);
                    }
                }
            }
        }

        return dp[n] == INT_MAX ? -1 : dp[n];
    }
};
