class Node {
    public:
        char val;
        bool isEnd;
        int cost;
        vector<Node*> ptrs;

        Node(char v){
            this->val = v;
            ptrs.resize(26);
            isEnd = false;
            cost = INT_MAX;
        }
};

class Solution {
public:
    Node* root;

    void helpInsert(string& word, int index, Node* r, int cost){
        if(index >= word.length()){
            r->isEnd = true;
            r->cost = min(r->cost, cost);
            return;
        }

        int cc = word[index]-'a';
        if(r->ptrs[cc] == NULL){
            Node* nn = new Node(word[index]);
            r->ptrs[cc] = nn;
        }
        helpInsert(word, index+1, r->ptrs[cc], cost);
    }

    void dfs(string& target, Node* r, int idx, vector<long long>& dp, int& i){
        if(r->isEnd){
            dp[i+1] = min(dp[i+1], r->cost + dp[idx+1]);
        }
        if(idx < 0){
            return;
        }

        int index = target[idx]-'a';
        if(r->ptrs[index] != NULL){
            dfs(target, r->ptrs[index], idx-1, dp, i);
        }
    }

    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
        root = new Node('0');
        int n = words.size();
        int len = target.length();
        for(int i = 0; i<n; i++){
            string a = words[i];
            reverse(a.begin(), a.end());
            // cout<<a<<endl;
            helpInsert(a, 0, root, costs[i]);
        }

        vector<long long> dp(len+10, INT_MAX);
        dp[0] = 0;
        long long ans = INT_MAX;
        for(int i = 0; i<len; i++){
            dfs(target, root, i, dp, i);

            // cout<<i<<"  "<<"   "<<dp[i+1]<<endl;
        }
        if(dp[len] == INT_MAX){
            return -1;
        }
        return dp[len];
    }
};
