#include <vector>
#include <string>
#include <climits>

using namespace std;

class TrieNode {
public:
    vector<TrieNode*> children;
    int cost;
    bool isEndOfWord;

    TrieNode() : children(26, nullptr), cost(INT_MAX), isEndOfWord(false) {}
};

class Trie {
public:
    TrieNode* root;

    Trie() {
        root = new TrieNode();
    }

    void addWord(const string& word, int cost) {
        TrieNode* currentNode = root;
        for (char c : word) {
            if (!currentNode->children[c - 'a']) {
                currentNode->children[c - 'a'] = new TrieNode();
            }
            currentNode = currentNode->children[c - 'a'];
        }
        currentNode->cost = min(cost, currentNode->cost);
        currentNode->isEndOfWord = true;
    }

    TrieNode* getRoot() {
        return root;
    }
};

class Solution {
public:
    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
        Trie trie;
        int targetLength = target.size();
        vector<int> minCost(targetLength + 1, INT_MAX);

        for (int i = 0; i < words.size(); ++i) {
            trie.addWord(words[i], costs[i]);
        }

        minCost[0] = 0;

        for (int i = 0; i < targetLength; ++i) {
            if (minCost[i] == INT_MAX) continue;

            TrieNode* currentNode = trie.getRoot();
            for (int j = i; j < targetLength; ++j) {
                char c = target[j];
                if (!currentNode->children[c - 'a']) break;
                currentNode = currentNode->children[c - 'a'];
                if (currentNode->isEndOfWord) {
                    minCost[j + 1] = min(minCost[j + 1], minCost[i] + currentNode->cost);
                }
            }
        }

        return minCost[targetLength] == INT_MAX ? -1 : minCost[targetLength];
    }
};
