#include <vector>
#include <string>
#include <climits>

using namespace std;

class TrieNode {
public:
    vector<TrieNode*> children;
    int wordCost;
    bool isEndOfWord;

    TrieNode() : children(26, nullptr), wordCost(INT_MAX), isEndOfWord(false) {}
};

class Trie {
public:
    TrieNode* root;

    Trie() {
        root = new TrieNode();
    }

    void addWord(const string& word, int cost) {
        TrieNode* currentNode = root;
        for (char letter : word) {
            if (!currentNode->children[letter - 'a']) {
                currentNode->children[letter - 'a'] = new TrieNode();
            }
            currentNode = currentNode->children[letter - 'a'];
        }
        currentNode->wordCost = min(cost, currentNode->wordCost);
        currentNode->isEndOfWord = true;
    }

    TrieNode* getRoot() {
        return root;
    }
};

class Solution {
public:
    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
        Trie trie;
        int targetLength = target.size();
        vector<int> minCost(targetLength + 1, INT_MAX);

        for (int i = 0; i < words.size(); ++i) {
            trie.addWord(words[i], costs[i]);
        }

        minCost[0] = 0;

        for (int startIndex = 0; startIndex < targetLength; ++startIndex) {
            if (minCost[startIndex] == INT_MAX) continue;

            TrieNode* currentNode = trie.getRoot();
            for (int endIndex = startIndex; endIndex < targetLength; ++endIndex) {
                char letter = target[endIndex];
                if (!currentNode->children[letter - 'a']) break;
                currentNode = currentNode->children[letter - 'a'];
                if (currentNode->isEndOfWord) {
                    minCost[endIndex + 1] = min(minCost[endIndex + 1], minCost[startIndex] + currentNode->wordCost);
                }
            }
        }

        return minCost[targetLength] == INT_MAX ? -1 : minCost[targetLength];
    }
};
