#include <vector>
#include <string>
#include <climits>

using namespace std;

class TrieNode {
public:
    vector<TrieNode*> children;
    int wordCost;
    bool isWordEnd;

    TrieNode() : children(26, nullptr), wordCost(INT_MAX), isWordEnd(false) {}
};

class Trie {
public:
    TrieNode* root;

    Trie() {
        root = new TrieNode();
    }

    void addWord(const string& word, int cost) {
        TrieNode* currentNode = root;
        for (char letter : word) {
            if (!currentNode->children[letter - 'a']) {
                currentNode->children[letter - 'a'] = new TrieNode();
            }
            currentNode = currentNode->children[letter - 'a'];
        }
        currentNode->wordCost = min(cost, currentNode->wordCost);
        currentNode->isWordEnd = true;
    }

    TrieNode* getRoot() {
        return root;
    }
};

class Solution {
public:
    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
        Trie trie;
        int targetLength = target.size();
        vector<int> costToReach(targetLength + 1, INT_MAX);

        for (int i = 0; i < words.size(); ++i) {
            trie.addWord(words[i], costs[i]);
        }

        costToReach[0] = 0;

        for (int i = 0; i < targetLength; ++i) {
            if (costToReach[i] == INT_MAX) continue;

            TrieNode* currentNode = trie.getRoot();
            for (int j = i; j < targetLength; ++j) {
                char letter = target[j];
                if (!currentNode->children[letter - 'a']) break;
                currentNode = currentNode->children[letter - 'a'];
                if (currentNode->isWordEnd) {
                    costToReach[j + 1] = min(costToReach[j + 1], costToReach[i] + currentNode->wordCost);
                }
            }
        }

        return costToReach[targetLength] == INT_MAX ? -1 : costToReach[targetLength];
    }
};
