class Solution {
public:
struct TrieNode {
    unordered_map<char, TrieNode*> children;
    int cost;
    TrieNode() : cost(INT_MAX) {}
};

// Function to insert a word into the trie with its cost
void insertWord(TrieNode* root, const string& word, int cost) {
    TrieNode* node = root;
    for (char c : word) {
        if (!node->children.count(c)) {
            node->children[c] = new TrieNode();
        }
        node = node->children[c];
    }
    node->cost = min(node->cost, cost);
}
    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
    int n = target.length();
    TrieNode* root = new TrieNode();

    // Insert all words into the trie
    for (int i = 0; i < words.size(); ++i) {
        insertWord(root, words[i], costs[i]);
    }

    vector<int> dp(n + 1, INT_MAX);
    dp[0] = 0;

    // Dynamic programming with trie to find minimum cost
    for (int i = 0; i < n; ++i) {
        if (dp[i] == INT_MAX) continue;
        TrieNode* node = root;
        for (int j = i; j < n; ++j) {
            if (!node->children.count(target[j])) break;
            node = node->children[target[j]];
            if (node->cost != INT_MAX) {
                dp[j + 1] = min(dp[j + 1], dp[i] + node->cost);
            }
        }
    }

    return dp[n] == INT_MAX ? -1 : dp[n];
    }
};
