from typing import List, Dict

class TrieNode:
    def __init__(self):
        self.children = {}
        self.cost = float('inf')  # Initialize cost as infinity

class Solution:
    def buildTrie(self, words: List[str], costs: List[int]) -> TrieNode:
        root = TrieNode()
        for word, cost in zip(words, costs):
            node = root
            for char in word:
                if char not in node.children:
                    node.children[char] = TrieNode()
                node = node.children[char]
            node.cost = min(node.cost, cost)  # Store the minimum cost for the word
        return root

    def minimumCost(self, target: str, words: List[str], costs: List[int]) -> int:
        n = len(target)
        dp = [float('inf')] * (n + 1)
        dp[0] = 0

        # Build the Trie from words and costs
        trie_root = self.buildTrie(words, costs)

        # Traverse the target string
        for i in range(n):
            if dp[i] == float('inf'):
                continue

            # Traverse the Trie from the current position
            node = trie_root
            for j in range(i, n):
                if target[j] not in node.children:
                    break
                node = node.children[target[j]]
                if node.cost != float('inf'):
                    dp[j + 1] = min(dp[j + 1], dp[i] + node.cost)

        return dp[n] if dp[n] != float('inf') else -1
