class TrieNode {
public:
    unordered_map<char, TrieNode*> children;
    vector<int> indexes; // Store the indexes of words ending at this node
    TrieNode() {}
};

class Trie {
public:
    TrieNode* root;
    Trie() {
        root = new TrieNode();
    }

    void insert(const string& word, int index) {
        TrieNode* node = root;
        for (char ch : word) {
            if (!node->children.count(ch)) {
                node->children[ch] = new TrieNode();
            }
            node = node->children[ch];
        }
        node->indexes.push_back(index);
    }
};

class Solution {
public:
    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
        int n = target.size();
        Trie trie;
        for (int i = 0; i < words.size(); ++i) {
            trie.insert(words[i], i);
        }

        vector<int> dp(n + 1, INT_MAX);
        dp[0] = 0;

        for (int i = 0; i < n; ++i) {
            if (dp[i] == INT_MAX) continue;

            TrieNode* node = trie.root;
            for (int j = i; j < n; ++j) {
                if (!node->children.count(target[j])) break;
                node = node->children[target[j]];
                for (int index : node->indexes) {
                    int wordLen = words[index].length();
                    dp[j + 1] = min(dp[j + 1], dp[i] + costs[index]);
                }
            }
        }

        return dp[n] == INT_MAX ? -1 : dp[n];

    }
};
