struct Node {
    unordered_map<char, Node*> children;
    int cost;
    Node() : cost(INT_MAX) {}
};

class Trie {
private:
    Node* root;
public:
    Trie() : root(new Node()) {}

    void insert(const string& word, int cost) {
        Node* node = root;
        for (char c : word) {
            if (!node->children.count(c)) {
                node->children[c] = new Node();
            }
            node = node->children[c];
        }
        node->cost = min(node->cost, cost); // Store the minimum cost
    }

    Node* getRoot() {
        return root;
    }
};
class Solution {
public:
    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
        int n = target.length();
        vector<int> dp(n + 1, INT_MAX);
        dp[0] = 0;

        Trie trie;
        for (int i = 0; i < words.size(); ++i) {
            trie.insert(words[i], costs[i]);
        }

        for (int i = 0; i < n; ++i) {
            if (dp[i] == INT_MAX) continue;
            Node* node = trie.getRoot();
            for (int j = i; j < n; ++j) {
                if (!node->children.count(target[j])) break;
                node = node->children[target[j]];
                if (node->cost != INT_MAX) {
                    dp[j + 1] = min(dp[j + 1], dp[i] + node->cost);
                }
            }
        }

        return dp[n] == INT_MAX ? -1 : dp[n];
    }
};
