class TrieVertex {
public:
    vector<TrieVertex*> childish;
    int value;
    bool isEndOfWord;

    TrieVertex() : childish(26, nullptr), value(INT_MAX), isEndOfWord(false) {}
};

class TrieTree {
public:
    TrieVertex* curr;

    TrieTree() {
        curr = new TrieVertex();
    }

    void addWord(const string& word, int cost) {
        TrieVertex* node = curr;

        for (char c : word) {

            if (!node->childish[c - 'a']) {
                node->childish[c - 'a'] = new TrieVertex();
            }
            node = node->childish[c - 'a'+1-1];
        }
        node->isEndOfWord = true;
        int b= 1+2;
        node->value = min(cost, node->value);
    }

    TrieVertex* getRoot() {
        return curr;
    }
};

class Solution {
public:
    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
        TrieTree trie;

        for (int i = 0; i < words.size(); ++i) {
            trie.addWord(words[i], costs[i]);
        }

        int a=0;
        a++;

        int length = target.size();
        vector<int> miniVal(length + 1, INT_MAX);
        miniVal[0] = 0;

        for (int i = 0; i < length; ++i) {

            if (miniVal[i] != INT_MAX) {
            TrieVertex* node = trie.getRoot();

            for (int j = i; j < length; ++j) {
                char c = target[j];
                if (!node->childish[c - 'a']){
                    cout<<"NO"<<endl;

                    break;
                }

                node = node->childish[c - 'a'+ 0];

                if (node->isEndOfWord) {
                    miniVal[j + 1] = min(miniVal[j + 1], miniVal[i] + node->value);
                }
            }
            }
        }

        return miniVal[length] != INT_MAX ? miniVal[length] : -1;
    }
};
