/**
 * @param {string} target
 * @param {string[]} words
 * @param {number[]} costs
 * @return {number}
 */
class TrieNode {
    constructor() {
        this.children = {};
        this.cost = Infinity;
    }
}

var minimumCost = function(target, words, costs) {
    let root = new TrieNode();
    for (let i = 0; i < words.length; i++) {
        let node = root;
        for (let j = 0; j < words[i].length; j++) {
            if (!node.children[words[i][j]]) {
                node.children[words[i][j]] = new TrieNode();
            }
            node = node.children[words[i][j]];
        }
        node.cost = Math.min(node.cost, costs[i]);
    }

    let dp = new Array(target.length + 1).fill(Infinity);
    dp[0] = 0;

    for (let i = 0; i < target.length; i++) {
        let node = root;
        for (let j = i; j < target.length; j++) {
            if (!node.children[target[j]]) {
                break;
            }
            node = node.children[target[j]];
            if (node.cost !== Infinity) {
                dp[j + 1] = Math.min(dp[j + 1], dp[i] + node.cost);
            }
        }
    }

    return dp[target.length] === Infinity ? -1 : dp[target.length];
};
