from typing import List

class TrieNode:
    def __init__(self):
        self.children = {}
        self.cost = float('inf')

class Trie:
    def __init__(self):
        self.root = TrieNode()

    def insert(self, word: str, cost: int):
        node = self.root
        for char in word:
            if char not in node.children:
                node.children[char] = TrieNode()
            node = node.children[char]
        node.cost = min(node.cost, cost)
class Solution:
    def minimumCost(self, target: str, words: List[str], costs: List[int]) -> int:
        n = len(target)
        trie = Trie()

        # 建立 Trie
        for word, cost in zip(words, costs):
            trie.insert(word, cost)

        # 初始化 dp 陣列
        dp = [float('inf')] * (n + 1)
        dp[0] = 0

        # 動態規劃結合 Trie
        for i in range(n):
            if dp[i] == float('inf'):
                continue
            node = trie.root
            for j in range(i, n):
                char = target[j]
                if char not in node.children:
                    break
                node = node.children[char]
                if node.cost != float('inf'):
                    dp[j + 1] = min(dp[j + 1], dp[i] + node.cost)

        # 返回結果
        return dp[n] if dp[n] != float('inf') else -1
