struct TrieNode {
    unordered_map<char, TrieNode*> children;
    int cost;
    TrieNode() : cost(INT_MAX) {}
};

class Trie {
public:
    TrieNode* root;
    Trie() { root = new TrieNode(); }

    void insert(const string& word, int cost) {
        TrieNode* node = root;
        for (char ch : word) {
            if (!node->children.count(ch)) {
                node->children[ch] = new TrieNode();
            }
            node = node->children[ch];
        }
        node->cost = min(node->cost, cost);
    }

    int findMinCost(const string& target, int start) {
        TrieNode* node = root;
        for (int i = start; i < target.size(); ++i) {
            char ch = target[i];
            if (!node->children.count(ch)) return INT_MAX;
            node = node->children[ch];
            if (node->cost != INT_MAX) {
                return node->cost;
            }
        }
        return INT_MAX;
    }
};

class Solution {
public:
    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
        int n = target.size();
        Trie trie;

        for (int i = 0; i < words.size(); ++i) {
            trie.insert(words[i], costs[i]);
        }

        vector<int> dp(n + 1, INT_MAX);
        dp[0] = 0; 

        for (int i = 0; i < n; ++i) {
            if (dp[i] == INT_MAX) {
                continue;
            }

            TrieNode* node = trie.root;
            for (int j = i; j < n; ++j) {
                if (!node->children.count(target[j])) break;
                node = node->children[target[j]];
                if (node->cost != INT_MAX) {
                    dp[j + 1] = min(dp[j + 1], dp[i] + node->cost);
                }
            }
        }

        return dp[n] == INT_MAX ? -1 : dp[n];
    }
};
