class TrieNode {
    Map<Character, TrieNode> children;
    int cost;

    TrieNode() {
        children = new HashMap<>();
        cost = Integer.MAX_VALUE;
    }
}

class Trie {
    TrieNode root;

    Trie() {
        root = new TrieNode();
    }

    void insert(String word, int cost) {
        TrieNode node = root;
        for (char c : word.toCharArray()) {
            node.children.putIfAbsent(c, new TrieNode());
            node = node.children.get(c);
        }
        node.cost = Math.min(node.cost, cost);
    }
}

class Solution {
    public int minimumCost(String target, String[] words, int[] costs) {
        int n = target.length();
        Trie trie = new Trie();

        for (int i = 0; i < words.length; ++i) {
            trie.insert(words[i], costs[i]);
        }

        int[] dp = new int[n + 1];
        for (int i = 0; i <= n; ++i) {
            dp[i] = Integer.MAX_VALUE;
        }
        dp[0] = 0;

        for (int i = 0; i < n; ++i) {
            if (dp[i] == Integer.MAX_VALUE) continue;
            TrieNode node = trie.root;
            for (int j = i; j < n; ++j) {
                char currentChar = target.charAt(j);
                if (!node.children.containsKey(currentChar)) break;
                node = node.children.get(currentChar);
                if (node.cost != Integer.MAX_VALUE) {
                    dp[j + 1] = Math.min(dp[j + 1], dp[i] + node.cost);
                }
            }
        }

        return dp[n] == Integer.MAX_VALUE ? -1 : dp[n];
    }
}
