class Trie {
public:
    int val;
    vector<Trie*> next;

    Trie(int val) : next(26, NULL) {
        this->val = val;
    }
};

class Solution {
public:
    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
        Trie* root = new Trie(-1);
        for (int i=0; i<words.size(); i++) {
            const string& s = words[i];
            Trie* head = root;
            for (char c : s) {
                if (head->next[c-'a'] == NULL) {
                    head->next[c-'a'] = new Trie(-1);
                }
                head = head->next[c-'a'];
            }
            if (head->val != -1) {
                head->val = min(costs[i], head->val);
            } else {
                head->val = costs[i];
            }
        }
        vector<int> dp(target.length()+1, -1);
        dp[0] = 0;
        for (int i=0; i<target.length(); i++) {
            if (dp[i] == -1) continue;
            Trie* head = root;
            for (int j=i; j<target.length(); j++) {
                if (head->next[target[j]-'a'] == NULL) {
                    break;
                }
                head = head->next[target[j]-'a'];
                if (head->val != -1) {
                    int nc = dp[i] + head->val;
                    // cout << j+1 << " " << nc << endl;
                    if (dp[j+1] == -1 || dp[j+1] > nc) {
                        dp[j+1] = nc;
                    }
                }
            }
        }
        return dp[target.length()];
    }
};
