class TrieNode {
public:
    TrieNode* children[26];
    int minCost;

    TrieNode() {
        for(int i = 0; i < 26; i++){
            children[i] = nullptr;
        }
        minCost = INT_MAX;
    }
};

class Trie {
public:
    TrieNode* root;

    Trie() {
        root = new TrieNode();
    }

    void insert(string& word, int cost) {
        TrieNode* node = root;
        for (char c : word) {
            int index = c - 'a';
            if (!node->children[index]) {
                node->children[index] = new TrieNode();
            }
            node = node->children[index];
        }
        node->minCost = min(node->minCost, cost);
    }

    int getMinCost(string& prefix) {
        TrieNode* node = root;
        for (char c : prefix) {
            int index = c - 'a';
            if (!node->children[index]) {
                return INT_MAX;
            }
            node = node->children[index];
        }
        return node->minCost;
    }
};

class Solution {
public:
    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
        Trie trie;
        for(int i = 0; i < words.size(); i++){
            trie.insert(words[i], costs[i]);
        }

        int n = target.size();
        vector<int> dp(n + 1, INT_MAX);
        dp[0] = 0;

        for(int i = 0; i < n; i++){
            if (dp[i] == INT_MAX) continue;
            TrieNode* node = trie.root;
            for (int j = i; j < n; ++j) {
                int index = target[j] - 'a';
                if (!node->children[index]) {
                    break;
                }
                node = node->children[index];
                if (node->minCost != INT_MAX) {
                    dp[j + 1] = min(dp[j + 1], dp[i] + node->minCost);
                }
            }
        }

        if(dp[n] == INT_MAX){
            return -1;
        }else{
            return dp[n];
        }
    }
};
