class TrieNode:
    def __init__(self):
        self.children = defaultdict(TrieNode)
        self.cost = float('inf')

class Solution:
    def build_trie(self, words: List[str], costs: List[int]) -> TrieNode:
        root = TrieNode()
        for word, cost in zip(words, costs):
            node = root
            for char in word:
                node = node.children[char]
            node.cost = min(node.cost, cost)  # In case of duplicate words, take the minimum cost
        return root

    def minimumCost(self, target: str, words: List[str], costs: List[int]) -> int:
        n = len(target)
        dp = [float('inf')] * (n + 1)
        dp[0] = 0

        # Build the trie
        root = self.build_trie(words, costs)

        for i in range(n):
            if dp[i] == float('inf'):
                continue
            # Try to match words starting from position i
            node = root
            for j in range(i, n):
                if target[j] not in node.children:
                    break
                node = node.children[target[j]]
                if node.cost < float('inf'):
                    dp[j + 1] = min(dp[j + 1], dp[i] + node.cost)

        return dp[n] if dp[n] != float('inf') else -1
