class Trie_Node{

    public:

    Trie_Node* child_nodes[26];
    int score;

    Trie_Node(){
        score = INT_MAX;
        std::fill(child_nodes, child_nodes + 26, nullptr);
    }


};



class Solution {
public:

    int Solve(std::string& str, int idx, Trie_Node* start, std::vector<int>& cache){

        if(idx >= str.size()){
            return 0;
        }    

        if(cache[idx] != -1){
            return cache[idx];
        }

        int result = (int)1e9;

        Trie_Node* current = start;
        for(int i = idx; i < str.size(); i++){

            if(current->child_nodes[str[i]-'a'] == nullptr){
                break;
            }

            current = current->child_nodes[str[i]-'a'];

            if(current->score != INT_MAX){
                int _score = current->score + Solve(str, i+1, start, cache);
                result = min(result, _score);
            }
        }

        return cache[idx] = result;
    }

    int minimumCost(std::string target, std::vector<std::string>& words, std::vector<int>& costs) {

        std::vector<int> cache(target.size(), -1);
        Trie_Node* start = new Trie_Node();        
        Trie_Node* current = nullptr;

        for(int i = 0; i < words.size(); i++){

            current = start;
            for(int j = 0; j < words[i].size(); j++){

                if(current->child_nodes[words[i][j]-'a'] == nullptr){
                    current->child_nodes[words[i][j] - 'a'] = new Trie_Node();
                }
                current = current->child_nodes[words[i][j]-'a'];
            }

            current->score = min(current->score, costs[i]);
        }

        int result = Solve(target, 0, start, cache);
        return (result < (int)1e9) ? result : -1;
    }
};
