class Node {
public:
    vector<Node*> child;
    int cost;
    bool isEnd;

    Node() : child(26, nullptr), cost(INT_MAX), isEnd(false) {}
};

class TrieTree {
public:
    Node* root;

    TrieTree() {
        root = new Node();
    }

    void insert(const string& word, int cost) {
        Node* node = root;
        for (char c : word) {
            if (!node->child[c - 'a']) {
                node->child[c - 'a'] = new Node();
            }
            node = node->child[c - 'a'];
        }
        node->cost = min(cost, node->cost);
        node->isEnd = true;
    }

    Node* getRoot() {
        return root;
    }
};

class Solution {
public:
    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
        TrieTree trie;
        int n = target.size();
        vector<int> dp(n + 1, INT_MAX);

        for (int i = 0; i < words.size(); ++i) {
            trie.insert(words[i], costs[i]);
        }

        dp[0] = 0;

        for (int i = 0; i < n; ++i) {
            if (dp[i] == INT_MAX) continue;

            Node* node = trie.getRoot();
            for (int j = i; j < n; ++j) {
                char c = target[j];
                if (!node->child[c - 'a']) break;
                node = node->child[c - 'a'];
                if (node->isEnd) {
                    dp[j + 1] = min(dp[j + 1], dp[i] + node->cost);
                }
            }
        }

        return dp[n] == INT_MAX ? -1 : dp[n];
    }
};
