class TrieNode:
    def __init__(self):
        self.children = {}
        self.cost = float('inf')

class Trie:
    def __init__(self):
        self.root = TrieNode()

    def insert(self, word, cost):
        node = self.root
        for char in word:
            if char not in node.children:
                node.children[char] = TrieNode()
            node = node.children[char]
        node.cost = min(node.cost, cost)  # Store the minimum cost for this word

    def search(self, target, start_index):
        node = self.root
        results = []
        for i in range(start_index, len(target)):
            if target[i] not in node.children:
                break
            node = node.children[target[i]]
            if node.cost != float('inf'):
                results.append((i + 1, node.cost))
        return results

class Solution:
    def minimumCost(self, target: str, words: List[str], costs: List[int]) -> int:
        target_length = len(target)
        dp = [float('inf')] * (target_length + 1)
        dp[0] = 0

        trie = Trie()
        for word, cost in zip(words, costs):
            trie.insert(word, cost)

        for i in range(target_length):
            if dp[i] == float('inf'):
                continue
            matches = trie.search(target, i)
            for end_index, cost in matches:
                dp[end_index] = min(dp[end_index], dp[i] + cost)

        return dp[target_length] if dp[target_length] != float('inf') else -1
