class TrieNode {
public:
    long long cost;
    TrieNode* children[26];
    TrieNode() : cost(INT_MAX), children{} {}
};
class Solution {
public:
    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
        TrieNode* root = new TrieNode();
        for (int i = 0; i < words.size(); ++i) {
            TrieNode* node = root;
            for (char c : words[i]) {
                if (!node->children[c - 'a']) node->children[c - 'a'] = new TrieNode();
                node = node->children[c - 'a'];
            }
            node->cost = node->cost<costs[i]?node->cost:costs[i];
        }

        vector<long long> dp(target.size() + 1, INT_MAX);
        dp[0] = 0;
        for (int i = 0; i < target.size(); ++i) {
            TrieNode* node = root;
            for (int j = i; j < target.size(); ++j) {
                node = node->children[target[j] - 'a'];
                if (!node) break;
                dp[j + 1] = dp[j + 1]<(dp[i] + node->cost)?dp[j+1]:(dp[i]+node->cost);
            }
        }

        return dp[target.size()] == INT_MAX ? -1 : dp[target.size()]; 
    }
};
