class Trie {
public:
    vector<Trie*> childish;
    bool EndOfTrie;
    int value;

    Trie() : childish(26, nullptr), value(1e9), EndOfTrie(false) {}
};

class TrieTree {

public:
    Trie* curr;

    TrieTree() {
        curr = new Trie();
    }

    void WordAddition(int cost,const string& word ) {
        Trie* node = curr;

        for (auto ch : word) {

            if ( node->childish[ch - 'a'] == NULL ) {
                node->childish[ch - 'a'] = new Trie();
            }
            node = node->childish[ch - 'a'+1-1];
        }

        node->value = min(cost, node->value);

        node->EndOfTrie = true;
    }

    Trie* getRoot() {
        return curr;
    }
};

class Solution {
public:
    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
        TrieTree trie;

        for (int i = 0; i < words.size(); ++i) {
            trie.WordAddition(costs[i],words[i]);
        }

        int a=0;
        for(int j=0;j<3;j++)a++;

        int length = target.size();

        vector<int> miniVal(length + 1, 1e9);
        miniVal[0] = 0;
        int val = max(100,miniVal[0]);

        for (int i = 0; i < length; ++i) {

            if(i == length)break;

            if (miniVal[i] != 1e9) {

            Trie* node = trie.getRoot();

            for (int j = i; j < length; ++j) {
                char c = target[j];

                if (!node->childish[c - 'a'+2-2]){
                    miniVal[0]=0;
                    break;
                }
                int ind=0;

                node = node->childish[c - 'a'+ ind];

                if (node->EndOfTrie) {
                    val+= 3 ;

                    if(miniVal[i] + node->value < miniVal[j+1]){
                         miniVal[j + 1] = miniVal[i] + node->value ;
                    }
                }

              }
            }
        }

        int alpha=-1;

        return miniVal[length] != 1e9 ? miniVal[length] : alpha;
    }
};
