class TrieVertex {
    TrieVertex[] children;
    int value;
    boolean isEndOfWord;

    TrieVertex() {
        children = new TrieVertex[26];
        value = Integer.MAX_VALUE;
        isEndOfWord = false;
    }
}

class TrieTree {
    TrieVertex root;

    TrieTree() {
        root = new TrieVertex();
    }

    void addWord(String word, int cost) {
        TrieVertex node = root;
        for (char c : word.toCharArray()) {
            if (node.children[c - 'a'] == null) {
                node.children[c - 'a'] = new TrieVertex();
            }
            node = node.children[c - 'a'];
        }
        node.value = Math.min(cost, node.value);
        node.isEndOfWord = true;
    }

    TrieVertex getRoot() {
        return root;
    }
}

public class Solution {
    public int minimumCost(String target, String[] words, int[] costs) {
        TrieTree trie = new TrieTree();
        int length = target.length();
        int[] minCost = new int[length + 1];
        Arrays.fill(minCost, Integer.MAX_VALUE);

        for (int i = 0; i < words.length; ++i) {
            trie.addWord(words[i], costs[i]);
        }

        minCost[0] = 0;

        for (int i = 0; i < length; ++i) {
            if (minCost[i] == Integer.MAX_VALUE) continue;

            TrieVertex node = trie.getRoot();
            for (int j = i; j < length; ++j) {
                char c = target.charAt(j);
                if (node.children[c - 'a'] == null) break;
                node = node.children[c - 'a'];
                if (node.isEndOfWord) {
                    minCost[j + 1] = Math.min(minCost[j + 1], minCost[i] + node.value);
                }
            }
        }

        return minCost[length] == Integer.MAX_VALUE ? -1 : minCost[length];
    }
}
